var notification = {};
var turn_off_notification = null;

var traveler = "Seth";
var destination = "Christy";
var phone = "christys_lg_g2";
var message = "";
var title = "";
var renotify = false;
if(msg.topic.endsWith("seth"))
{
    phone = "seths_one_plus_3t";
    traveler = "Christy";
    destination = "Seth";
}
if(Number(msg.payload) < 3)
{
    title = traveler+" is here.";
    message = "Travel time is 2 minutes or less.";
    renotify = true;
    var entity_id = "input_boolean.notify_travel_time_"+traveler.toLowerCase()+"_to_"+destination.toLowerCase();
    turn_off_notification = {payload: {"data":{"entity_id": entity_id}}};
}
else
{
    title = traveler+" will be here in "+msg.payload+" minutes.";
    message = "Estimated travel time.";
}
var tag = traveler+"_to_"+destination;
notification = {payload: {"data": {"target": phone, "title": title, "message": message, "data":{"tag":tag, "renotify":renotify}}}};

return [notification, turn_off_notification];

var list1 = msg.payload[0].split('\n');
var count = list1.length;
if(msg.payload.length > 1)
{
    list2 = msg.payload[1].split('\n');
    count = Math.abs(list1.length - list2.length);
}

var result = {payload: {
                "state":count ,
                "attributes":{"friendly_name": "Number of Guests"}
                },
              topic: "Number of Guests"};
return result;

var t1 = Date.parse(msg.data.old_state.last_changed);
var t2 = Date.parse(msg.data.new_state.last_changed);

//time delta in minutes
var delta = ((t2-t1)/1000.0)/60.0;
msg.dT = delta;
return msg;

var returnMsgs = [];
for(i = 0; i < msg.payload.length; i++)
{
    var newMsg = {filename: msg.payload[i],
                  gitPath: msg.payload[i].replace("/home/hass/.node-red/", "")};
    returnMsgs.push(newMsg);
}
return [returnMsgs];
